Text Only<pre><code>func insertSort(arr []int){
    if arr == nil || len(arr) &lt; 2 {
        return    
    }    
    for i := 1;i &lt; len(arr);i++{
        //当前值为j+1,前一个值为j        
        //每一轮，当前值(j+1)往前看,如果当前值的位置到0,或者,前面的大于当前值时才交换
        for j := i-1; j &gt;= 0; j-- {
            if arr[j] &lt;= arr[j+1] {
                break            
            }
            swap(arr,j,j+1)       
        } 
    }
}
