Text Only<pre><code>func selectSort(arr []int){
    if arr == nil || len(arr) &lt; 2 {
        return    
    }    
    //每轮找到最小的那个值,与当前第一位进行交换,进行N轮查找    
    for i := 0;i &lt; len(arr);i++{
        min,index := i,i        
        //每一轮查找        
        for j := i+1;j &lt; len(arr);j++{
            if arr[j] &lt; arr[min] { 
                min = j            
            }        
        }        
        swap(arr,min,index)
        index++   
    }
}
</code></pre>

Text Only<pre><code>func bubbleSort(arr []int){    
    if arr == nil || len(arr) &lt; 2 {
        return    
    }    
    //每一轮确定一个位置    
    for i := 0;i &lt; len(arr);i++{
        //当前值与下一位进行比较,如果当前值比下一位值大,则交换        
        for j := 0;j &lt; len(arr)-1;j++{            
            if arr[j] &gt; arr[j+1] {                
                swap(arr,j,j+1)            
            }        
        }    
    }
}
