Text Only<pre><code>func sortArray(nums []int) []int {
    quickSort(nums, 0, len(nums)-1)
    return nums
}

// 递归调用，经典二分
func quickSort(nums []int, left, right int) {
    if left &gt;= right {
        return
    }
    mid := partition(nums, left, right)
    quickSort(nums, left, mid-1)
    quickSort(nums, mid+1, right)
}

// 分区函数，快排核心
func partition(nums []int, left, right int) int {
    // 选择 left 作为基准
    standard, start := nums[left], left
    for left &lt; right {
        // 右边先出发
        for left &lt; right &amp;&amp; nums[right] &gt;= standard {
            right--
        }
        // 左边也出发
        for left &lt; right &amp;&amp; nums[left] &lt;= standard {
            left++
        }
        // 相遇结束
        if left &gt;= right {
            break
        }
        // 否则交换两个数
        swap(nums, left, right)
    }
    // 把基准归位
    swap(nums, start, left)
    // 返回基准位置
    return left
}
</code></pre>

Text Only<pre><code>func sortArray(nums []int) []int {
    return mergeSort(nums, 0, len(nums)-1)
}

// 递归调用, 经典二分
func mergeSort(nums []int, left, right int) []int {
    if left == right {
        return []int{nums[left]}
    }
    mid := (left + right) / 2
    arr1 := mergeSort(nums, left, mid)
    arr2 := mergeSort(nums, mid+1, right)
    return merge(arr1, arr2)
}

// 归并排序核心, 将两个有序数组做归并
func merge(arr1, arr2 []int) []int {
    temp, i, j := make([]int, 0), 0, 0
    // 如果两个数组都有值，则依次取首位做比较
    for len(arr1) &gt; 0 &amp;&amp; len(arr2) &gt; 0 {
        if i &gt;= len(arr1) || j &gt;= len(arr2) {
            break
        }
        if arr1[i] &gt; arr2[j] {
            temp = append(temp, arr2[j])
            j++
        } else {
            temp = append(temp, arr1[i])
            i++
        }
    }
    // 程序执行到这里时，一定有一方数组被掏空了
    for i &lt; len(arr1) {
        temp = append(temp, arr1[i])
        i++
    }
    for j &lt; len(arr2) {
        temp = append(temp, arr2[j])
        j++
    }
    return temp
}
