    heapSort(nums)
    return nums
}

func heapSort(nums []int) {
    len := len(nums) - 1
    //建堆
    buildMaxHeap(nums, len)
    //
    for i := len; i &gt;= 1; i-- {
        swap(nums, i, 0)
        len -= 1
        maxHeapify(nums, 0, len)
    }
}

//从下往上建堆,O(N) 并且后1/2的节点都是叶子节点,天然满足大根堆的性质
func buildMaxHeap(nums []int, len int) {
    for i := len / 2; i &gt;= 0; i-- {
        maxHeapify(nums, i, len)
    }
}

func maxHeapify(nums []int, i, len int) {
    for (2*i)+1 &lt;= len {
        lson := (2*i) + 1
        rson := (2*i) + 2
        large := 0
        if lson &lt;= len &amp;&amp; nums[lson] &gt; nums[i] {
            large = lson
        } else {
            large = i
        }
        if rson &lt;= len &amp;&amp; nums[rson] &gt; nums[large] {
            large = rson
        }
        if large != i {
            swap(nums, i, large)
            i = large
        } else {
            break
        }
    }
}
</code></pre>

Text Only<pre><code>func countSort(nums []int) []int {
    // 先找到边界
    max := 0
    for _, v := range nums {
        if v &gt; max {
            max = v
        }
    }
    // 建桶
    array := make([]int, max+1)
    // 将原数组的数据入桶
    for _, v := range nums {
        array[v] += 1
    }
    // 用桶里的数据覆盖原nums数组
    index := 0
    for i, v := range array {
        for v &gt; 0 {
            nums[index] = i
            index++
            v--
        }
    }
    return nums
}
</code></pre>

